import { useState } from "react";
import { Link } from "react-router-dom"
import { Button, Container, Form, FormGroup, Input, Label } from "reactstrap"

export const CadastrarServico = () => {

    const [servico, setServico] = useState({
        nome: '',
        descricao: ''
    });

    const valorInput = e => setServico({
        ...servico, [e.target.name]: e.target.value
    })

    const cadServico = async e => {
        console.log(servico);
        e.preventDefault();

        await axios.post(api + "/servicos")
            .then((response) => {
                console.log(response.data.servicos);
                setData(response.data.servicos);
            })
            //o catch vai tratar o erro -> o setStatus vai escrever qual erro ocorreu dentro do type e da message
            .catch(() => {
                setStatus({
                    type: 'error',
                    message: 'Erro: Não foi possível conectar a API'
                })
            });
    };

    return (
        <div>
            <Container>
                <div className="d-flex">
                    <div className="mr-auto p-2">
                        <h1>Cadastrar Serviço</h1>
                    </div>
                    <div className="p-2">
                        <Link to="/visualizarservico" className="btn btn-outline-success btn-sm">Voltar para a lista de Serviços</Link>
                    </div>
                </div>
                {/* linha hr =m1 */}
                <hr className="m-1" />

                <Form className="p-2" onSubmit={cadServico}>
                    <FormGroup className="p-2">
                        <Label>Nome</Label>
                        <Input type="text" name="nome" placeholder="Nome do serviço" onChange={valorInput} />
                    </FormGroup>
                </Form>

                <Form className="p-2" onSubmit={cadServico}>
                    <FormGroup className="p-2">
                        <Label>Descrição</Label>
                        <Input type="text" name="nome" placeholder="Descrição do serviço" onChange={valorInput} />
                    </FormGroup>
                    <Button type="submit" outline color="success">Cadastrar</Button>
                </Form>

            </Container>

        </div>
    )
}
