//ir na aplicação Controller.js alguma aplicação que tenha servico, que seja um GET e q seja passado por um id. Testar no postman, substituir o id pelo numero do servico desejado para trazer as informacoes somente do servico
import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Alert, Container } from "reactstrap";
import { api } from "../../../config";


export const Servico0 = (props) => {
    //console.log(props.match.params.id);

    const [data, setData] = useState([]);
    const [status, setStatus] = useState([]);
    const [id, setID] = useState(props.match.params.id);

    useEffect(() => {
        const getServico = async () => {
            await axios.get(api + "/servico/" + id)
                .then((response) => {
                    console.log(response.data.servico)
                    setData(response.data.servico)
                })
                .catch(() => {
                    setStatus({
                        type: 'error',
                        message: 'Erro: Não foi possível conectar a API.'
                    })
                })
        }
        getServico();
    }, [id]);

    return (
        <div>
            <Container>
                <div className="d-flex">
                    <div className="mr-auto p-2">
                        <h1>Informações do Serviço</h1>
                    </div>
                    <div className="p-2">
                        <Link to="/visualizarservico" className="btn btn-outline-success btn-sm">Voltar para a lista de Serviços</Link>
                    </div>
                </div>
                <hr className="m1" />
                {status.type === 'error' ? <Alert color="danger">{status.message}</Alert> : ""}

                <dl className="row">
                    <dt className="col-sm-3">Nome</dt>
                    <dd className="col-sm-9">{data.nome}</dd>
                </dl>
                <dl className="row">
                    <dt className="col-sm-3">Descrição</dt>
                    <dd className="col-sm-9">{data.descricao}</dd>
                </dl>
            </Container>
        </div>

    )
}
