import axios from 'axios';
import { Container, Table } from "reactstrap"
import { useEffect, useState } from 'react';

import { api } from '../../../config';

export const VisualizarServico = () => {

    const [data, setData] = useState([]);

    const getServicos = async () => {
        await axios.get(api + "/listaservicos")
            .then((response) => {
                console.log(response.data.servicos);
                setData(response.data.servicos);
            }).catch(() => {
                console.log("Erro: Não foi possível conectar a API")
            })
    }


useEffect(() => {
    getServicos();
}, []);

return (
    <div className="p-3">
        <Container>
            <Table dark hover>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Serviços</th>
                        <th>Descrição</th>
                        <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map(item =>(
                        <tr>
                        <td>{item.id}</td>
                        <td>{item.nome}</td>
                        <td>{item.descricao}</td>
                        <td className="text-center">Bot˜</td>
                        </tr>
                    ))}
                    <tr>
                        <th scope="row">1</th>
                        <td>HTML/CSS</td>
                        <td>JavaScript</td>
                        <td>Node Js</td>
                    </tr>
                    <tr>
                        <th scope="row">2</th>
                        <td>Aprender a estruturar modelos em Html</td>
                        <td>Thornton</td>
                        <td>@fat</td>
                    </tr>
                    <tr>
                        <th scope="row">3</th>
                        <td>Front</td>
                        <td>Front</td>
                        <td>@twitter</td>
                    </tr>
                </tbody>

            </Table>

        </Container>

    </div>
)
}
