import { useState } from 'react';
import { Link } from 'react-router-dom';
import { Alert, Button, Container, Form, FormGroup, Input, Label, Spinner } from 'reactstrap';

export const EditarServico = (props) => {

    const [id] = useState(props.match.params.id);
    const [nome, setNome] = useState('');
    const [descricao, setDescricao] = useState('');

    const [status, setStatus] = useState({
        formSave: false,
        type: '',
        message: ''
    });

    return (
        <div>
            <Container>
                <div className="d-flex">
                    <div className="m-auto p-2">
                        <h1>Editar um Serviço</h1>
                    </div>
                    <div>
                        <Link to={"/visualizarservico/"}
                            className="btn btn-outline-primary btn-sm m-1">Voltar para Lista de Serviços</Link>
                        <Link to={"/servico0/" + id}
                            className="btn btn-outline-primary btn-sm">Consultar</Link>
                    </div>
                </div>

                <hr className="m-1" />
                {status.type === 'error' ? <Alert color="warning">{status.message}</Alert> : ""}
                {status.type === 'success' ? <Alert color="success">{status.message}</Alert> : ""}

                <Form className="p-2" onSubmit={edtServico}>
                    <FormGroup className="p-2">
                        <Label>Nome</Label>
                        <Input type="text" name="nome" placeholder="Nome do serviço" />
                    </FormGroup>
                </Form>

                <Form className="p-2">
                    <FormGroup className="p-2">
                        <Label>Descrição</Label>
                        <Input type="text" name="descricao" placeholder="Descrição do serviço" />
                    </FormGroup>

                    {status.formSave ? <Button type="submit" outline color="warning" disabled>Cadastrando...<Spinner style={{ width: '3rem', height: '3rem' }} type="grow" /></Button> :
                        <Button type="submit" outline color="success">Cadastrar</Button>}
                    <Button type="reset" outline color="success">Limpar</Button>
                </Form>

            </Container>
        </div>
    )
}
